import { useState, useEffect } from "react";
import Task from "./Task";
import axios from "axios";

export default function Tasks({
    Status,
    Tasks,
    setTasks,
    setAddTaskFlag,
    setStatusValue,
    setTitleValue,
}) {
    const [filteredTasks, setFilteredTasks] = useState([]);

    useEffect(() => {
        const fetchFilteredTasks = async () => {
            try {
                const response = await axios.get(`/api/todolist/${Status}`);
                setFilteredTasks(response.data);
            } catch (error) {
                console.error(
                    "Error fetching filtered tasks from the API: ",
                    error
                );
            }
        };
        fetchFilteredTasks();
    }, [Status]);
    console.log(filteredTasks);

    const TasksComponent = filteredTasks.map((taskClass, index) => (
        <Task
            key={index}
            Key={index}
            TaskName={taskClass.task}
            Status={taskClass.status}
            setTasks={setTasks}
            setAddTaskFlag={setAddTaskFlag}
            setTitleValue={setTitleValue}
            setStatusValue={setStatusValue}
        />
    ));

    return (
        <div className="TasksBodyDiv">
            {TasksComponent.length === 0 ? (
                <p className="NoTodos">No Todos</p>
            ) : (
                TasksComponent
            )}
        </div>
    );
}
